import React, { useEffect, useState } from "react";
import { useParams, useHistory } from "react-router-dom"
import { readCard } from "../utils/api/index"

export default function EditCard(){
    const { cardId } = useParams();
    const [card, setCard] = useState({})
    async function getCard(){
        setCard(await readCard(cardId));
    }
    useEffect(() => {
        getCard()
    }, [])
    const history = useHistory();
    async function submitHandler(event) {
        event.preventDefault();
        const card = {
            front: event.target.front.value,
            back: event.target.back.value,
        };
        const newCard = await createCard(deckId, card);
        history.push(`/decks/${deckId}`)
    }
    return (
        <form onSubmit={submitHandler}>
            <div class="form-group">
                <label for="description">Description</label>
                <textarea
                    class="form-control"
                    name="front"
                    id="front"
                    rows="3"
                    defaultValue={card.front}></textarea>
            </div>
            <div class="form-group">
                <label for="description">Description</label>
                <textarea
                    class="form-control"
                    name="back"
                    id="back"
                    rows="3"
                    defaultValue={card.back}></textarea>
            </div>
            <button type="cancel" class="btn btn-secondary mr-2">Done</button>
            <button type="submit" class="btn btn-primary">Save</button>
        </form>
    )
}
