import React, { useState, useEffect} from "react"
import { Link, useHistory } from "react-router-dom"
import { listCards, deleteDeck } from "../utils/api/index"

export default function DisplayDeck({deck = {}, deckPage = false, getDecks}){
    const [cards, setCards] = useState([]);
    const abortController = new AbortController();
	const signal = abortController.signal;
    const history = useHistory();

    async function getCards() {
            try {
                const response = await listCards(deck.id, signal);
                setCards(response);
            }
            catch(error) {
                if(error.name !== "AbortError") {
                    throw error;
                }
            }
        }

    useEffect(() => {
            getCards();

            return () => {
                abortController.abort();
            };
        }, []);

    async function deleteHandler(id){
        if(window.confirm("Are you sure you want to delete?")){
            await deleteDeck(id);
            if(getDecks){
                getDecks();
            }
            history.push("/");
        }
        
    }
    if(deckPage){
        return (
            <div className="mb-2">
                <h3 className="card-title">{deck.name}</h3>
                <p className="card-text">{deck.description}</p>
                <a href="#" className="btn btn-secondary mr-2">
                    <button className="btn text-light" style={{fontSize: "15pt"}}>
                        <i className="fa fa-pencil mr-2"></i>Edit
                    </button>
                </a>
                <Link to={`/decks/${deck.id}/study`} className="btn btn-primary mr-2">
                    <button className="btn text-light" style={{fontSize: "15pt"}}>
                        <i className="fa fa-book mr-2"></i>
                        <a className="text-white" style={{textDecoration: "none"}} to={`/decks/${deck.id}/study`}>
                            Study
                        </a>
                    </button>
                </Link>
                <a href="#" className="btn btn-primary mr-2">
                    <button className="btn text-light" style={{fontSize: "15pt"}}>
                        <i className="fa fa-plus mr-2"></i>Add Cards
                    </button>
                </a>
                <a href="#" className="btn btn-danger float-right">
                    <button className="btn text-light" style={{fontSize: "15pt"}}>
                        <i className="fa fa-trash"></i>
                    </button>
                </a>
            </div>
            
        )
    }
    return (
        <div className="card">
            <div className="card-body">
                <row className="d-flex justify-content-between">
                    <h5 className="card-title">{deck.name}</h5>
                    <p>{cards.length} cards</p>
                </row>
                <p className="card-text">{deck.description}</p>
                <div>
                    <Link to={`/decks/${deck.id}`} className="btn btn-secondary mr-2">
                        <button className="btn text-light" style={{fontSize: "15pt"}}>
                            <i className="fa fa-eye mr-2"></i>View
                        </button>
                    </Link>
                    <Link to={`/decks/${deck.id}/study`} className="btn btn-primary mr-2">
                        <button className="btn text-light" style={{fontSize: "15pt"}}>
                            <i className="fa fa-book mr-2"></i>
                            <a className="text-white" style={{textDecoration: "none"}} to={`/decks/${deck.id}/study`}>
                                Study
                            </a>
                        </button>
                    </Link>
                    <a href="#" className="btn btn-danger float-right">
                        <button className="btn text-light" style={{fontSize: "15pt"}} onClick={() => deleteHandler(deck.id)}>
                            <i className="fa fa-trash"></i>
                        </button>
                    </a>
                </div>
            </div>
        </div>
    )
}
