import React from "react";
import { Link, useParams, useRouteMatch } from "react-router-dom"

export default function Breadcrumb({page, deck}){
    const url = useRouteMatch();
    const { deckId } = useParams();
    if(page.includes("study")){
        return (
            <nav aria-label="breadcrumb">
                <ol class="breadcrumb">
                    <li class="breadcrumb-item" aria-current="page"><Link to="/">Home</Link></li>
                    <li class="breadcrumb-item" aria-current="page"><Link to={`/decks/${deckId}`}>{deck.name}</Link></li>
                    <li class="breadcrumb-item active" aria-current="page">Study</li>
                </ol>
            </nav>
        )
    }
    return (
        <nav aria-label="breadcrumb">
            <ol class="breadcrumb">
                <li class="breadcrumb-item" aria-current="page"><Link to="/">Home</Link></li>
            </ol>
        </nav>
    )
}
