import React, { useState }  from "react";
import { useParams, useHistory } from "react-router-dom";

export default function DisplayCard({flip, handleFlip, cards, decks}){
    const { deckId } = useParams();
    const currentDeck = decks.find(deck => deck.id == deckId)
    const cardList = cards.filter(card => card.deckId == deckId)
    let [currentIndex, setCurrentIndex] = useState(0)
    const history = useHistory();
    const next = () => setCurrentIndex((newVal) => {
        newVal = currentIndex + 1;
        if(cardList[newVal]){
            handleFlip();
            return newVal;
        }
        else{
            if(window.confirm("Wanna start again? Pressing cancel will return you to the home screen.")){
                handleFlip();
                return 0;
            }
            else{
                history.push("/");
                handleFlip();
                return 0;
            }
        }
    })
    if(cardList.length <= 2){
        return (
            <div>
                <h1>Not enough cards.</h1>
                <p>You need at least 3 cards to study. There are {cardList.length}</p>
                <a href="#" className="btn btn-primary mr-2">
                    <button className="btn text-light" style={{fontSize: "15pt"}}>
                        <i className="fa fa-plus mr-2"></i>Add Cards
                    </button>
                </a>
            </div>
        )
    }
    if(flip === false){
        return (
            <>
                <h1>Study: {currentDeck.name}</h1>
                <div className="card">
                    <div className="card-body">
                        <h5 className="card-title">Card {currentIndex+1} of {cardList.length}</h5>
                        <p className="card-text">{cardList[currentIndex].front}</p>
                        <div>
                            <button className="btn btn-secondary" onClick={handleFlip} style={{fontSize: "15pt"}}>
                                <p style={{paddingLeft: "10px", paddingRight: "10px", margin: "5px"}}>Flip</p>
                            </button>
                        </div>
                    </div>
                </div>
            </>
        )
    }

    return (
        <>
            <h1>Study: {currentDeck.name}</h1>
            <div className="card">
                <div className="card-body">
                    <h5 className="card-title">Card {currentIndex+1} of {cardList.length}</h5>
                    <p className="card-text">{cardList[currentIndex].back}</p>
                    <div>
                        <button className="btn btn-secondary mr-2" onClick={handleFlip} style={{fontSize: "15pt"}}>
                        <p style={{paddingLeft: "10px", paddingRight: "10px", margin: "5px"}}>Flip</p>
                        </button>
                        <button className="btn btn-primary" onClick={next} style={{fontSize: "15pt"}}>
                        <p style={{paddingLeft: "10px", paddingRight: "10px", margin: "5px"}}>Next</p>
                        </button>
                    </div>
                </div>
            </div>
        </>
    )
    
}
