import React, { useState }  from "react";
import { Route, Switch, useHistory, useParams } from "react-router-dom";
import Header from "./Header";
import NotFound from "./NotFound";
import CreateDeck from "./CreateDeck"
import DisplayCard from "./DisplayCard"
import NewDeckForm from "./NewDeckForm"
import ListDecks from "./ListDecks";
import Deck from "./Deck"

function Layout() {
  const [flip, setFlip] = useState(false)
  const { deckId } = useParams()
  const handleFlip = () => setFlip((val) => val = !flip)
  let [currentIndex, setCurrentIndex] = useState(0)
  const history = useHistory();
  const next = (cardList) => setCurrentIndex((newVal) => {
    newVal = currentIndex + 1;
    if(cardList[newVal]){
        handleFlip();
        return newVal;
    }
    else{
        const startAgain = window.confirm("Wanna start again? Pressing cancel will return you to the home screen.")
        if(startAgain){
            handleFlip();
            return 0;
        }
        else{
            history.push("/");
            handleFlip();
            return 0;
        }
    }
})

  const decks = [{
    "id": 1,
    "name": "Rendering in React",
    "description": "Blah Blah Blah Blah Blah Blah Blah Blah Blah Blah Blah Blah Blah Blah Blah Blah Blah Blah",
  },{
    "id": 2,
    "name": "Routes and stuff",
    "description": "Yah Yah Yah Yah Yah Yah Yah Yah Yah Yah Yah Yah Yah Yah Yah Yah Yah Yah Yah Yah Yah Yah ",
  }]

  const cards = [{
    "id": 1,
    "deckId": 1,
    "front": "????????????????????????????",
    "back": "Blah Blah Blah Blah Blah Blah Blah Blah Blah Blah Blah Blah Blah Blah Blah Blah Blah ",
},
{
  "id": 3,
  "deckId": 1,
  "front": "????????????????????????????",
  "back": "lah Blah lah lah Blah lah Blah lah lah Blah lah Blah lah Blah lah Blah lah ",
},
{
  "id": 3,
  "deckId": 1,
  "front": "????????????????????????????",
  "back": "Blh Blh Bah Blh Blh Bla Blah Blh Bla Bla BlahBlah Bla BlahBlah Blah Blh ",
}]


  return (
    <>
      <Header />

      <div className="container">
        <Switch>
          <Route path="/" exact={true}>
            <CreateDeck />
            <ListDecks decks={decks} cards={cards}/>
          </Route>
          <Route path="/decks/:deckId/study">
            <DisplayCard 
              flip={flip} 
              handleFlip={handleFlip}
              currentIndex={currentIndex}
              next={next}
              cards={cards}
              decks={decks}/>
          </Route>

          <Route path="/decks/:deckId/">
            <Deck decks={decks}/>
          </Route>

          <Route>
            <NotFound />
          </Route>
        </Switch>
        {/* TODO: Implement the screen starting here */}


      </div>
    </>
  );
}

export default Layout;
